mapper.update:
UPDATE COMMUNITY
   SET COMMUNITY_VIEWS = COMMUNITY_VIEWS + 1
 WHERE COMMUNITY_NO = #{communityNo}

UPDATE COMMUNITY
   SET COMMUNITY_STATUS = 'N'
 WHERE COMMUNITY_NO = #{communityNo}

UPDATE COMMUNITY
   SET COMMUNITY_GOOD = COMMUNITY_GOOD + 1
 WHERE COMMUNITY_NO = #{communityNo}

UPDATE COMMUNITY
   SET COMMUNITY_BAD = COMMUNITY_BAD + 1
 WHERE COMMUNITY_NO = #{communityNo}

UPDATE COMMUNITY
   SET COMMUNITY_CATEGORY = #{communityCategory},
   	   COMMUNITY_TITLE = #{communityTitle},
   	   COMMUNITY_DETAIL = #{communityDetail}
 WHERE COMMUNITY_NO = #{communityNo}

UPDATE BOARD_COMMENT
   SET COMMENT_DETAIL = #{commentDetail}
 WHERE COMMENT_NO = #{commentNo}
